import React from 'react'; 
import { getFunName } from '../helpers';

class StoredPicker extends React.Component{
    //Rever constructor
    constructor(props){
        super(props);
        console.log(props);
        this.state = {value: ''};
        console.log(this.state);

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        alert('Um nome foi enviado: ' + this.state.value);
        event.preventDefault();
    }

    render(){
        //return React.createElement('p', {className: 'hey'}, 'Heyoooo');
        return  (
                <form action="" className="store-selector" value={this.state.value} onChange={this.handleChange}>
                    <h2> Please enter a store</h2>
                    <input 
                        type="text" 
                        required 
                        placeholder= "Store Name" 
                        defaultValue={getFunName()} 
                    />
                    <button type="submit">Visite Store</button>
               </form>
        )
    }
}

export default StoredPicker;
